// Show/hide password toggle only. Password strength indicator removed.
document.addEventListener('DOMContentLoaded', function(){
	var inputPasswordField = document.querySelector('input.password');
	var showPasswordBtn = document.querySelector('div.show-pass');
	if(!inputPasswordField || !showPasswordBtn) return;
	// set initial icon
	showPasswordBtn.innerHTML = '<i class="feather feather-eye"></i>';
	showPasswordBtn.addEventListener('click', function(){
		if(inputPasswordField.getAttribute('type') === 'password'){
			inputPasswordField.setAttribute('type','text');
			showPasswordBtn.innerHTML = '<i class="feather feather-eye-off"></i>';
		} else {
			inputPasswordField.setAttribute('type','password');
			showPasswordBtn.innerHTML = '<i class="feather feather-eye"></i>';
		}
	});
});
